mapper.select:
SELECT t.* FROM (
SELECT *, DENSE_RANK() OVER (PARTITION BY standingbook_id ORDER BY sync_time DESC) AS rnk
FROM collect_raw_data
WHERE standingbook_id IN #{standingbookIds}
) t WHERE t.rnk = 1
ORDER BY sync_time DESC

SELECT *
FROM collect_raw_data
WHERE standingbook_id in #{standingbookId}
AND `usage` = 1
AND sync_time = #{targetTime}
LIMIT 1

SELECT cr.* FROM collect_raw_data cr JOIN ( SELECT standingbook_id, MAX(sync_time) AS max_time FROM collect_raw_data WHERE standingbook_id in
#{id}
AND `usage` = 1 AND sync_time < #{targetTime} GROUP BY standingbook_id ) latest ON cr.standingbook_id = latest.standingbook_id AND cr.sync_time = latest.max_time

SELECT cr.* FROM collect_raw_data cr JOIN ( SELECT standingbook_id, MIN(sync_time) AS min_time FROM collect_raw_data WHERE standingbook_id IN
#{id}
AND `usage` = 1 AND sync_time > #{targetTime} GROUP BY standingbook_id ) latest ON cr.standingbook_id = latest.standingbook_id AND cr.sync_time = latest.min_time
