mapper.select:
SELECT t.* FROM ( SELECT *, DENSE_RANK() OVER (PARTITION BY standingbook_id ORDER BY sync_time DESC) AS rnk FROM collect_raw_data WHERE standingbook_id IN
#{id}
) t WHERE t.rnk = 1 ORDER BY sync_time DESC

SELECT *
FROM (
SELECT *,
ROW_NUMBER() OVER (
PARTITION BY standingbook_id, param_code, energy_flag
ORDER BY sync_time DESC
) AS rn
FROM collect_raw_data
WHERE sync_time >= #{startMinute}
AND sync_time <  #{endMinute}
AND data_feature = 2  -- 只取稳态值
AND `usage` = 0
) t
WHERE rn = 1;

SELECT * FROM collect_raw_data WHERE standingbook_id in
#{id}
AND `usage` = 1 AND sync_time = #{targetTime} LIMIT 1

SELECT distinct cr.data_site,cr.sync_time,cr.param_code,cr.energy_flag,cr.standingbook_id,cr.calc_value,cr.raw_value,cr.collect_time, cr.full_increment,cr.data_feature,cr.data_type,cr.usage FROM collect_raw_data cr JOIN ( SELECT standingbook_id, MAX(sync_time) AS max_time FROM collect_raw_data WHERE standingbook_id in
#{id}
AND `usage` = 1 AND sync_time < #{targetTime} GROUP BY standingbook_id ) latest ON cr.standingbook_id = latest.standingbook_id and `usage`=1 AND cr.sync_time = latest.max_time

SELECT distinct cr.data_site,cr.sync_time,cr.param_code,cr.energy_flag,cr.standingbook_id,cr.calc_value,cr.raw_value,cr.collect_time,cr.full_increment,cr.data_feature,cr.data_type,cr.usage FROM collect_raw_data cr JOIN ( SELECT standingbook_id, MIN(sync_time) AS min_time FROM collect_raw_data WHERE standingbook_id IN
#{id}
AND `usage` = 1 AND sync_time > #{targetTime} GROUP BY standingbook_id ) latest ON cr.standingbook_id = latest.standingbook_id and `usage`= 1 AND cr.sync_time = latest.min_time
