mapper.update:
UPDATE ems_coal_factor_history
SET end_time = #{endTime},
    updater = #{updater},
    update_time = #{updateTime}
WHERE id = #{id}

mapper.select:
SELECT h.energy_id, h.factor, h.start_time, h.end_time, h.formula_id, f.energy_formula, f.formula_scale FROM ems_coal_factor_history h LEFT JOIN ems_da_param_formula f ON h.formula_id = f.id WHERE h.energy_id IN
#{id}
AND h.start_time <= #{queryTime} AND (h.end_time IS NULL OR #{queryTime} <= h.end_time)
