mapper.update:
UPDATE ems_coal_factor_history
SET end_time = #{endTime},
    updater = #{updater},
    update_time = #{updateTime}
WHERE id = #{id}

mapper.select:
SELECT h.energy_id, h.factor, h.start_time, h.end_time, h.formula_id, t.query_time AS aggregateTime FROM ems_coal_factor_history h JOIN (
SELECT #{param.energyId} AS energy_id, #{param.aggregateTime} AS query_time
) t ON h.energy_id = t.energy_id WHERE h.start_time <= t.query_time AND (h.end_time IS NULL OR t.query_time <= h.end_time) and h.deleted = 0
