mapper.select:
select a.id, a.energy_formula, if(a.formula_type = 1,
( ifnull( (select 1 from ems_coal_factor_history ecfh where ecfh.deleted = 0 and ecfh.formula_id = a.id and ecfh.end_time is null limit 1), ifnull( (select 2 from ems_coal_factor_history ecfh where ecfh.deleted = 0 and ecfh.formula_id = a.id and ecfh.start_time is not null and ecfh.end_time is not null limit 1), 0) )),
(if((select 1 from ems_unit_price_configuration eupc where eupc.deleted = 0 and eupc.formula_id = a.id limit 1), (ifnull( (select 2 from ems_unit_price_configuration eupc where eupc.deleted = 0 and eupc.formula_id = a.id and eupc.end_time < now() limit 1), 1)), 0) ) ) as formulaStatus from (select edpf.id, edpf.energy_formula, edpf.formula_type from ems_da_param_formula edpf
edpf.deleted = 0 and edpf.id in
#{id}
) as a
