sql.where:
ws.deleted = 0

    and ws.`level` = #{reqVO.level}


    and ws.name like CONCAT('%',#{reqVO.name},'%')


    and su.username like CONCAT('%',#{reqVO.creatorName},'%')


    and ws.create_time BETWEEN #{reqVO.createTime[0]} and #{reqVO.createTime[1]}

mapper.select:
SELECT ws.*, su.username creatorName FROM `power_warning_strategy` ws LEFT JOIN system_users su ON ws.creator = su.id
order by ws.create_time desc
limit #{reqVO.pageSize} OFFSET #{offset}
